import {IBPSuckerDeployer} from "../interfaces/IBPSuckerDeployer.sol";
import {BPTokenMapping} from "./BPTokenMapping.sol";

struct BPSuckerDeployerConfig {
    IBPSuckerDeployer deployer;
    BPTokenMapping[] mappings;
}

